import Accordion from './accordion'; 

// --- Data for the Accordion ---
const faqData = [
  {
    title: 'What are the key features of React?',
    content:
      "React's key features include its component-based architecture, virtual DOM for performance, JSX for templating, and one-way data flow. It can also be rendered on the server side, making it flexible for various use cases.",
  },
  {
    title: 'How does Tailwind CSS improve development speed?',
    content:
      'Tailwind CSS accelerates development by providing a vast library of utility classes that can be applied directly in your HTML. This eliminates the need to write custom CSS for most styling, allowing for rapid prototyping and consistent design.',
  },
  {
    title: 'What are best practices for accessibility (a11y)?',
    content:
      'Best practices for accessibility include using semantic HTML (e.g., <button>, <nav>), providing text alternatives for images (alt text), ensuring sufficient color contrast, enabling keyboard navigation, and using ARIA roles and attributes where necessary.',
  },
  {
    title: 'How do you manage state in a complex React application?',
    content:
      'For complex applications, state can be managed using built-in hooks like `useReducer` and `useContext` for medium-sized apps, or dedicated state management libraries like Redux, Zustand, or MobX for larger, more intricate state requirements.',
  },
];

// --- The Page Component ---
export default function AccordionPage() {
  return (
    <div className='flex flex-col items-center justify-center font-sans p-4 transition-colors duration-500'>
      <div className='w-full max-w-2xl mx-auto'>
        <h1 className='text-4xl font-bold text-gray-900 dark:text-slate-100 text-center mb-2'>
          Modern Accordion
        </h1>
        <p className='text-gray-600 dark:text-slate-400 text-center mb-10'>
          A production-ready, accessible, and stylish accordion component.
        </p>
        
        {/* Here we use the reusable component with our data */}
        <Accordion items={faqData} />

      </div>
    </div>
  );
}

"use client";
import React, { useState, useMemo, useCallback, memo } from "react";

interface AccordionItemData {
  title: string;
  content: string;
}

interface AccordionProps {
  items: AccordionItemData[];
}

interface AccordionItemProps {
  title: string;
  content: string;
  isOpen: boolean;
  onClick: () => void;
  isLast: boolean;
}

const AccordionItem = memo(function AccordionItem({
  title,
  content,
  isOpen,
  onClick,
  isLast,
}: AccordionItemProps) {
  const uniqueId = useMemo(() => title.replace(/\s+/g, "-"), [title]);

  const containerClasses = useMemo(
    () => (!isLast ? "border-b border-gray-200 dark:border-slate-700" : ""),
    [isLast]
  );

  const buttonClasses = useMemo(
    () =>
      "w-full flex justify-between items-center p-5 text-left text-lg font-medium text-gray-800 dark:text-slate-200 hover:bg-gray-100 dark:hover:bg-slate-700/50 focus:outline-none focus-visible:ring focus-visible:ring-indigo-500 focus-visible:ring-opacity-75 transition-colors duration-300",
    []
  );

  const iconClasses = useMemo(
    () =>
      `w-5 h-5 text-gray-500 dark:text-slate-400 transition-transform duration-300 ${isOpen ? "rotate-45" : ""}`,
    [isOpen]
  );

  const contentClasses = useMemo(
    () =>
      `grid overflow-hidden transition-all duration-500 ease-in-out ${isOpen ? "grid-rows-[1fr] opacity-100" : "grid-rows-[0fr] opacity-0"}`,
    [isOpen]
  );

  return (
    <div className={containerClasses}>
      <button
        type="button"
        className={buttonClasses}
        onClick={onClick}
        aria-expanded={isOpen}
        aria-controls={`accordion-content-${uniqueId}`}
        id={`accordion-header-${uniqueId}`}
      >
        <span>{title}</span>
        <div className="w-6 h-6 flex-shrink-0 flex items-center justify-center">
          <svg
            className={iconClasses}
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            strokeWidth={2}
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              d="M12 4.5v15m7.5-7.5h-15"
            />
          </svg>
        </div>
      </button>

      <div
        id={`accordion-content-${uniqueId}`}
        role="region"
        aria-labelledby={`accordion-header-${uniqueId}`}
        className={contentClasses}
      >
        <div className="overflow-hidden">
          <div className="p-5 pt-2 text-gray-600 dark:text-slate-400">
            <p>{content}</p>
          </div>
        </div>
      </div>
    </div>
  );
});

export default function Accordion({ items }: AccordionProps) {
  const [openIndex, setOpenIndex] = useState<number | null>(null);

  const handleClick = useCallback((index: number) => {
    setOpenIndex((prevIndex) => (prevIndex === index ? null : index));
  }, []);

  const containerClasses = useMemo(
    () =>
      "rounded-xl shadow-lg bg-gray-50 dark:bg-slate-800/60 border border-gray-200 dark:border-slate-700 backdrop-blur-sm",
    []
  );

  return (
    <div className={containerClasses}>
      {items.map((item, index) => (
        <AccordionItem
          key={`${item.title}-${index}`}
          title={item.title}
          content={item.content}
          isOpen={openIndex === index}
          onClick={() => handleClick(index)}
          isLast={index === items.length - 1}
        />
      ))}
    </div>
  );
}

"use client";

import React, { useState } from "react";
import {
  Briefcase,
  Shuffle,
  AlertCircle,
  BookOpen,
  Wallet,
  Mail,
  ShieldCheck,
  MonitorPlay,
  ChevronDown,
} from "lucide-react";

// Data for the FAQ section, including icons
const faqData = [
  {
    icon: <Briefcase size={20} className="text-gray-500 dark:text-gray-400" />,
    question: "How do I create an account?",
    answer:
      'To create an account, simply click the "Sign Up" button at the top-right corner of the page and fill in your details. It only takes a minute!',
  },
  {
    icon: <Shuffle size={20} className="text-gray-500 dark:text-gray-400" />,
    question: "Can I change my subscription plan?",
    answer:
      "Yes, you can upgrade or downgrade your subscription plan at any time from your account settings. Changes will be prorated.",
  },
  {
    icon: (
      <AlertCircle size={20} className="text-gray-500 dark:text-gray-400" />
    ),
    question: "What happens if I forget my password?",
    answer:
      "Don't worry! You can easily reset your password by clicking the 'Forgot Password' link on the login page. We'll send a reset link to your email.",
  },
  {
    icon: <BookOpen size={20} className="text-gray-500 dark:text-gray-400" />,
    question: "Where can I find the user guides?",
    answer:
      'All our user guides and documentation are available in the "Help" section of our website. You can also search for specific topics.',
  },
  {
    icon: <Wallet size={20} className="text-gray-500 dark:text-gray-400" />,
    question: "What payment methods do you accept?",
    answer:
      "We accept all major credit cards, including Visa, MasterCard, and American Express. We also support payments via PayPal.",
  },
  {
    icon: <Mail size={20} className="text-gray-500 dark:text-gray-400" />,
    question: "How can I contact customer support?",
    answer:
      "Our support team is available 24/7. You can reach us via the contact form on our website, by email at support@example.com, or through live chat.",
  },
  {
    icon: (
      <ShieldCheck size={20} className="text-gray-500 dark:text-gray-400" />
    ),
    question: "Is my personal data secure?",
    answer:
      "Absolutely. We prioritize your privacy and security. We use state-of-the-art encryption and security protocols to protect all your data.",
  },
  {
    icon: (
      <MonitorPlay size={20} className="text-gray-500 dark:text-gray-400" />
    ),
    question: "Do you have video tutorials?",
    answer:
      'Yes, we have a library of video tutorials that cover all the main features of our platform. You can find them on our YouTube channel and in the "Tutorials" section.',
  },
];


interface AccordionItemProps {
  item: {
    icon: React.ReactNode;
    question: string;
    answer: string;
  };
  isOpen: boolean;
  onToggle: () => void;
}

const AccordionItem: React.FC<AccordionItemProps> = ({
  item,
  isOpen,
  onToggle,
}) => {
  return (
    <div className="border-b border-gray-200 dark:border-gray-700 last:border-b-0">
      <button
        className="flex items-center justify-between w-full p-4 text-left focus:outline-none hover:bg-gray-50 dark:hover:bg-gray-800 transition-colors"
        onClick={onToggle}
      >
        <div className="flex items-center space-x-4">
          {item.icon}
          <span className="text-base font-medium text-gray-800 dark:text-gray-200">
            {item.question}
          </span>
        </div>
        <ChevronDown
          size={20}
          className={`transform transition-transform duration-300 text-gray-500 dark:text-gray-400 ${isOpen ? "rotate-180" : ""}`}
        />
      </button>
      <div
        className={`overflow-hidden transition-all duration-300 ease-in-out ${
          isOpen ? "max-h-96" : "max-h-0"
        }`}
      >
        <div className="p-4 pl-12">
          <p className="text-gray-600 dark:text-gray-300">{item.answer}</p>
        </div>
      </div>
    </div>
  );
};


export default function AccordionSection() {
  const [openIndex, setOpenIndex] = useState<number | null>(2); 

  const handleToggle = (index: number) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  return (
    <div className="flex items-center justify-center">
      <div className="w-full max-w-2xl mx-auto">
        <div className="p-4">
          <h1 className="text-2xl font-bold text-center text-gray-800 dark:text-gray-100 mb-6">
            General Questions
          </h1>
          <div className="border border-gray-200 dark:border-gray-700 rounded-lg">
            {faqData.map((item, index) => (
              <AccordionItem
                key={index}
                item={item}
                isOpen={openIndex === index}
                onToggle={() => handleToggle(index)}
              />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

"use client"
import React, { useState } from 'react';

// Type definitions
interface FAQItem {
  question: string;
  answer: string;
}

interface AccordionItemProps {
  question: string;
  answer: string;
  isOpen: boolean;
  onClick: () => void;
}

// Updated data for the FAQ section
const faqData: FAQItem[] = [
  {
    question: 'How do I get started?',
    answer: 'Getting started is easy! Just follow the simple steps outlined in our comprehensive setup guide, which will walk you through the entire process from beginning to end.',
  },
  {
    question: 'Where can I find my account details?',
    answer: 'You can find all of your account information, including your profile, settings, and billing history, on the \'My Account\' page once you have successfully logged in.',
  },
  {
    question: 'How do I reset my password?',
    answer: 'To reset your password, please click the "Forgot Password" link on the login page. We will then send an email with further instructions to your registered email address.',
  },
  {
    question: 'Who can I contact for support?',
    answer: 'Our dedicated support team is available around the clock to assist you. You can reach us through the contact form on our website or by sending an email to support@example.com.',
  },
];

// AccordionItem component for each FAQ entry
const AccordionItem: React.FC<AccordionItemProps> = ({ question, answer, isOpen, onClick }) => {
  return (
    <div className="bg-white/90 dark:bg-black/90 rounded-xl shadow-lg border border-gray-200 dark:border-green-500/60 mb-4 overflow-hidden backdrop-blur-md relative">
      {/* Subtle gradient overlay */}
      <div className="absolute inset-0 bg-gradient-to-br from-gray-50/50 to-white/30 dark:from-green-400/15 dark:to-green-500/10 rounded-xl pointer-events-none"></div>
      
      <button
        className="w-full flex justify-between items-center text-left p-6 focus:outline-none hover:bg-gray-50 dark:hover:bg-green-800/30 transition-colors duration-200 relative z-10"
        onClick={onClick}
      >
        <span className="text-lg font-medium text-gray-900 dark:text-green-50">{question}</span>
        {/* Chevron Icon with a smoother, longer transition */}
        <svg
          className={`w-6 h-6 text-gray-600 dark:text-green-300 transform transition-transform duration-500 ease-in-out ${
            isOpen ? 'rotate-180' : ''
          }`}
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M19 9l-7 7-7-7"
          />
        </svg>
      </button>
      {/* Collapsible content area with a smoother, longer transition */}
      <div
        className={`overflow-hidden transition-all duration-500 ease-in-out ${
          isOpen ? 'max-h-96' : 'max-h-0'
        }`}
      >
        {/* Answer text with a fade-in effect for a smoother appearance */}
        <div className="p-6 pt-0 relative z-10">
          <p className={`text-gray-700 dark:text-green-100 leading-relaxed transition-opacity duration-300 ease-in-out ${isOpen ? 'opacity-100 delay-200' : 'opacity-0'}`}>
            {answer}
          </p>
        </div>
      </div>
    </div>
  );
};

// Main App component that renders the FAQ section
export default function CustomAccordion() {
  const [openIndex, setOpenIndex] = useState<number | null>(0); // First item is open by default

  const handleItemClick = (index: number) => {
    // If the clicked item is already open, close it. Otherwise, open it.
    setOpenIndex(openIndex === index ? null : index);
  };

  return (
    <div className="flex items-center justify-center font-sans p-4 rk:bg-black ">
      <div className="w-full max-w-2xl mx-auto">
        <div className="text-center mb-10">
          <span className="bg-[#EBF3FB] dark:bg-blue-900/20 text-[#3B82F6] dark:text-blue-400 text-sm font-medium px-4 py-1.5 rounded-full">
            Help Center
          </span>
          <h1 className="text-4xl font-bold text-gray-800 dark:text-white mt-4">
            Common Questions
          </h1>
        </div>
        <div>
          {faqData.map((faq, index) => (
            <AccordionItem
              key={index}
              question={faq.question}
              answer={faq.answer}
              isOpen={openIndex === index}
              onClick={() => handleItemClick(index)}
            />
          ))}
        </div>
      </div>
    </div>
  );
}
